import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault(); // ✅ prevent form reload

    if (username && password) {
      try {
        const res = await axios.post('https://localhost:5001/api/Auth/Login', {
          username,
          password
        });

        console.log(res);

        if (res.data.message === "Login successful") {
          navigate('/dashboard'); // ✅ redirect
        } else {
          alert(res.data.message);
        }
      } catch (err) {
        console.error(err);
        alert('Login failed: ' + (err.response?.data || err.message));
      }
    } else {
      alert('Please enter both username and password.');
    }
  };

  return (
    <>
      <div className="login-select">
        <div className="login-mdv">
          <div className="login-sbdv">
            <div className="login-item">
              <img
                src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.svg"
                className="img-fluid"
                alt="Phone"
              />
              <form className="form-inputs" onSubmit={handleLogin}>
                <div className="form-outline mb-4">
                  <input
                    type="text"
                    id="UserId"
                    placeholder="Enter Email ID"
                    className="form-control form-control-lg"
                    required
                    onChange={(e) => setUsername(e.target.value)}
                  />
                  <label className="form-label bi bi-envelope-at-fill" htmlFor="UserId">
                    Email ID
                  </label>
                </div>

                <div className="form-outline mb-4">
                  <input
                    type="password"
                    id="Password"
                    placeholder="Password"
                    className="form-control form-control-lg"
                    required
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  <label className="form-label" htmlFor="Password">
                    Password
                  </label>
                </div>

                <div className="form-options">
                  <div className="form-check">
                    <input
                      className="form-check-input"
                      type="checkbox"
                      id="form1Example3"
                      defaultChecked
                    />
                    <label className="form-check-label" htmlFor="form1Example3">
                      Remember me
                    </label>
                  </div>
                  <a href="#!" className="forgot-password">
                    Forgot password?
                  </a>
                </div>

                <button type="submit" className="btn-submit">
                  Sign in
                </button>

                <div className="divider">
                  <p>OR</p>
                </div>

                <button className="btn-social facebook" type="button">
                  <i className="fab fa-facebook-f"></i> Continue with Facebook
                </button>
                <button className="btn-social twitter" type="button">
                  <i className="fab fa-twitter"></i> Continue with Twitter
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
