// src/components/Employee/EmployeeList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './EmployeeList.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
function EmployeeList() {
  const departments = ["Engineering", "Marketing", "Sales", "HR"];
  const designations = ["Manager", "Developer", "Designer", "Analyst"];
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({ id: 0, name: '', email: '', phone: '', department: '', designation: '' });

  const fetchEmployees = async () => {
    const res = await axios.get('https://localhost:5001/api/Employee');
    setEmployees(res.data);
  };

  const handleSubmit = async () => {
    if (form.id === 0) {
      await axios.post('https://localhost:5001/api/Employee', form);
    } else {
      await axios.put(`https://localhost:5001/api/Employee/${form.id}`, form);
    }
    fetchEmployees();
    setForm({ id: 0, name: '', email: '', phone: '', department: '', designation: '' });
  };

  const handleEdit = (emp) => {
    setForm(emp);
  };

  const handleDelete = async (id) => {
    await axios.delete(`https://localhost:5001/api/Employee/${id}`);
    fetchEmployees();
  };
  const handleClose = () => {
    setForm({ id: 0, name: '', email: '', phone: '', department: '', designation: '' });
  };
  useEffect(() => {
    fetchEmployees();
  }, []);

  return (
    <div className='emp-main-form'>
      <h2> Add Employee </h2>
      <div className='emp-input'>
        <input placeholder=" Enter Name" required value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })}  />
        <input placeholder=" Enter Email" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} required/>
        <input placeholder=" Enter Phone" value={form.phone} onChange={(e) => setForm({ ...form, phone: e.target.value })}  required/>
        
        {/* Department Dropdown */}
        <select value={form.department} onChange={(e) => setForm({ ...form, department: e.target.value })} required>
          <option value="">Select Department</option>
          {departments.map((dept, index) => (
            <option key={index} value={dept}>{dept}</option>
          ))}
        </select>

        {/* Designation Dropdown */}
        <select value={form.designation} onChange={(e) => setForm({ ...form, designation: e.target.value })} required>
          <option value="">Select Designation</option>
          {designations.map((desig, index) => (
            <option key={index} value={desig}>{desig}</option>
          ))}
        </select>
        <button className='btn-sub-up' onClick={handleSubmit} >{form.id === 0 ? 'Add' : 'Update'} Employee</button>
        <button className='btn-close' onClick={handleClose}>Close</button>
      </div>
      <h2 style={{ marginTop: '2rem' }}>Employee List</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ background: '#f0f0f0' }}>
            <th style={cellStyle}>Name</th>
            <th style={cellStyle}>Email</th>
            <th style={cellStyle}>Phone</th>
            <th style={cellStyle}>Department</th>
            <th style={cellStyle}>Designation</th>
            <th style={cellStyle}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(emp => (
            <tr key={emp.id}>
              <td style={cellStyle}>{emp.name}</td>
              <td style={cellStyle}>{emp.email}</td>
              <td style={cellStyle}>{emp.phone}</td>
              <td style={cellStyle}>{emp.department}</td>
              <td style={cellStyle}>{emp.designation}</td>
              <td style={{width:'100px'}}>
                <button onClick={() => handleEdit(emp)} style={actionBtn}>
                <FontAwesomeIcon icon={faEdit} />
                </button>             
                 <button onClick={() => handleDelete(emp.id)} style={{ ...actionBtn, background: 'red' }}>
                 <FontAwesomeIcon icon={faTrash} />
                 </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
// Inline styles for table
const cellStyle = {
  border: '1px solid #ddd',
  padding: '0.5rem',
  textAlign: 'left'
};

const actionBtn = {
  padding: '0.3rem 0.6rem',
  marginRight: '0.5rem',
  background: '#4f46e5',
  color: 'white',
  border: 'none',
  cursor: 'pointer'
};
export default EmployeeList;
