// src/components/Login.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

import { Link } from 'react-router-dom';
function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
        debugger
      const res = await axios.post('https://localhost:44357/api/Auth/login', {
        username,
        password
      });
      alert(res.data.message);
      navigate('/dashboard');
    } catch (err) {
      alert('Login failed');
    }
  };

  return (
    <>
     <div className="login-select">
      <div className="login-mdv">
        <div className="login-sbdv">
          <div className="login-item">
            <img
              src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.svg"
              className="img-fluid"
              alt="Phone"
            />
            <form className="form-inputs">
              {/* Email input */}
              <div className="form-outline mb-4">
                <input type="text" placeholder=" Email "  className="form-control form-control-lg" required onChange={(e) => setUsername(e.target.value)} />
                <label className="form-label" htmlFor="form1Example13">
                  Email ID
                </label>
              </div>

              {/* Password input */}
              <div className="form-outline mb-4">
                    <input type="password" placeholder="Password"  className="form-control form-control-lg" onChange={(e) => setPassword(e.target.value)} />
                <label className="form-label" htmlFor="form1Example23">
                  Password
                </label>
              </div>

              {/* Remember Me + Forgot */}
              <div className="form-options">
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    id="form1Example3"
                    defaultChecked
                  />
                  <label className="form-check-label" htmlFor="form1Example3">
                    Remember me
                  </label>
                </div>
                <a href="#!" className="forgot-password">
                  Forgot password?
                </a>
              </div>

              {/* Submit button */}
              <button type="submit" className="btn-submit">
                Sign in
              </button>
              <div className="divider">
                <p>OR</p>
               </div>
              <button className="btn-social facebook">
                <i className="fab fa-facebook-f"></i> Continue with Facebook
              </button>
              <button className="btn-social twitter">
                <i className="fab fa-twitter"></i> Continue with Twitter
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
      {/* <div>
      <h2>Login</h2>
      <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
    </div> */}
    </>
  
  );
}

export default Login;
