// src/components/Employee/EmployeeList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './EmployeeList.css';

function EmployeeList() {
  
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({ id: 0, name: '', email: '', phone: '', department: '', designation: '' });

  const fetchEmployees = async () => {
    const res = await axios.get('https://localhost:5001/api/Employee');
    setEmployees(res.data);
  };

  const handleSubmit = async () => {
    if (form.id === 0) {
      await axios.post('https://localhost:5001/api/Employee', form);
    } else {
      await axios.put(`https://localhost:5001/api/Employee/${form.id}`, form);
    }
    fetchEmployees();
    setForm({ id: 0, name: '', email: '', phone: '', department: '', designation: '' });
  };

  const handleEdit = (emp) => {
    setForm(emp);
  };

  const handleDelete = async (id) => {
    await axios.delete(`https://localhost:5001/api/Employee/${id}`);
    fetchEmployees();
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  return (
    <div className='emp-main-form'>
      <h2>Employee List</h2>
      <div className='emp-input'>
        <input placeholder="Name" required value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })}  />
        <input placeholder="Email" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} required/>
        <input placeholder="Phone" value={form.phone} onChange={(e) => setForm({ ...form, phone: e.target.value })}  required/>
        
        {/* Department Dropdown */}
        <select value={form.department} onChange={(e) => setForm({ ...form, department: e.target.value })} required>
          <option value="">Select Department</option>
          {departments.map((dept, index) => (
            <option key={index} value={dept}>{dept}</option>
          ))}
        </select>

        {/* Designation Dropdown */}
        <select value={form.designation} onChange={(e) => setForm({ ...form, designation: e.target.value })} required>
          <option value="">Select Designation</option>
          {designations.map((desig, index) => (
            <option key={index} value={desig}>{desig}</option>
          ))}
        </select>
        <input placeholder="Department" value={form.department} onChange={(e) => setForm({ ...form, department: e.target.value })}  required />
        <input placeholder="Designation" value={form.designation} onChange={(e) => setForm({ ...form, designation: e.target.value })}  required/>
        <button onClick={handleSubmit} style={{ padding: '0.5rem', background: '#4f46e5', color: 'white' }}>{form.id === 0 ? 'Add' : 'Update'} Employee</button>
      </div>

      <ul>
        {employees.map(emp => (
          <li key={emp.id}>
            {emp.name} - {emp.email}
            <button onClick={() => handleEdit(emp)} style={{ marginLeft: '1rem', background: 'orange', color: 'white', padding: '0.25rem 0.5rem' }}>Edit</button>
            <button onClick={() => handleDelete(emp.id)} style={{ marginLeft: '0.5rem', background: 'red', color: 'white', padding: '0.25rem 0.5rem' }}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EmployeeList;
