// src/components/Employee/EmployeeList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({ id: 0, name: '', email: '', phone: '', department: '', designation: '' });

  const fetchEmployees = async () => {
    const res = await axios.get('https://localhost:5001/api/Employee');
    setEmployees(res.data);
  };

  const handleSubmit = async () => {
    if (form.id === 0) {
      await axios.post('https://localhost:5001/api/Employee', form);
    } else {
      await axios.put(`https://localhost:5001/api/Employee/${form.id}`, form);
    }
    fetchEmployees();
    setForm({ id: 0, name: '', email: '', phone: '', department: '', designation: '' });
  };

  const handleEdit = (emp) => {
    setForm(emp);
  };

  const handleDelete = async (id) => {
    await axios.delete(`https://localhost:5001/api/Employee/${id}`);
    fetchEmployees();
  };
  const handClose = async () => {
      navigate('/dashboard');
  };
  useEffect(() => {
    fetchEmployees();
  }, []);

  return (
    <div>
      <h2>Employee List</h2>
      <input placeholder="Name" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
      <input placeholder="Email" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} />
      <input placeholder="Phone" value={form.phone} onChange={(e) => setForm({ ...form, phone: e.target.value })} />
      <input placeholder="Department" value={form.department} onChange={(e) => setForm({ ...form, department: e.target.value })} />
      <input placeholder="Designation" value={form.designation} onChange={(e) => setForm({ ...form, designation: e.target.value })} />
      <button onClick={handleSubmit}>{form.id === 0 ? 'Add' : 'Update'} Employee</button>

      <ul>
        {employees.map(emp => (
          <li key={emp.id}>
            {emp.name} - {emp.email} 
            <button onClick={() => handleEdit(emp)}>Edit</button>
            <button onClick={() => handleDelete(emp.id)}>Delete</button>
          </li>
        ))}
      </ul>
        <button onClick={handClose}>Login</button>
    </div>
  );
}

export default EmployeeList;
